import * as readline from 'node:readline/promises';
import { stdin as input, stdout as output } from 'node:process';
import chalk from 'chalk';

const rl = readline.createInterface({input, output});

const correctEmail = 'admin@vcs.lt';
const correctPassword = 'Github12';

 async function login(){

    const email = await rl.question('Įveskite el.pašto adresa: ');
    const password = await rl.question('Įveskite slaptažodį: ');
    

        if(!email && !password){
            console.log(chalk.yellow('Negavome jokių duomenų, bandykite dar kartą.'));
        } else if(!email || !password){
            console.log(chalk.yellow('Negavome jokių duomenų, bandykite dar kartą.'));
        } else if( email === correctEmail && password === correctPassword){
            console.log(chalk.green('Sveikiname sėkmingai prisijungus!'));
        } else {
            console.log(chalk.red('Neteisingi prisijungimo duomenys.'));
            
        }

        rl.close();
}

login();

